import { useState, useEffect } from "react";
import PropTypes from "prop-types";

// @mui material components
import Card from "@mui/material/Card";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";

// Data
import DataTable from "examples/Tables/DataTable";

// Componente para célula personalizada
const CustomCell = ({ value }) => (
  <MDTypography
    component="a"
    href="#"
    variant="caption"
    color="text"
    fontWeight="medium"
    sx={{ cursor: "default" }}
  >
    {value.text}
  </MDTypography>
);

// Definindo PropTypes para o componente CustomCell
CustomCell.propTypes = {
  value: PropTypes.shape({
    text: PropTypes.string.isRequired,
  }).isRequired,
};

function LastDonations() {
  const [doacoesData, setDoacoesData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("authToken");
        if (!token) {
          throw new Error("Nenhum token de autenticação encontrado");
        }

        const response = await fetch(`${process.env.API_URL}/api/apheresisProcedures`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        const dados = data.map((doacao) => {
          const dataProcedimento = new Date(doacao.DataProcedimento);
          // Formatando a data no formato dd/mm/yy
          const dataFormatada = `${String(dataProcedimento.getDate()).padStart(2, "0")}/${String(
            dataProcedimento.getMonth() + 1
          ).padStart(2, "0")}/${dataProcedimento.getFullYear().toString().slice(-2)}`;
          // Formatando a hora no formato hh:mm
          const horaFormatada = `${String(dataProcedimento.getHours()).padStart(2, "0")}:${String(
            dataProcedimento.getMinutes()
          ).padStart(2, "0")}`;
          return {
            codigo: doacao.CodigoDoacao,
            data: dataFormatada,
            hora: horaFormatada,
          };
        });
        setDoacoesData(dados);
      } catch (error) {
        console.error("Erro ao buscar dados:", error);
      }
    };

    fetchData();
  }, []);

  const columns = [
    {
      Header: "Código doação",
      accessor: "codigo",
      width: "40%",
      Cell: (cellProps) => <CustomCell value={{ text: cellProps.value }} />,
    },
    {
      Header: "Data",
      accessor: "data",
      width: "30%",
      Cell: (cellProps) => <CustomCell value={{ text: cellProps.value }} />,
    },
    {
      Header: "Hora Inicio",
      accessor: "hora",
      width: "30%",
      Cell: (cellProps) => <CustomCell value={{ text: cellProps.value }} />,
    },
  ];

  return (
    <Card>
      <MDBox display="flex" justifyContent="space-between" alignItems="center" p={3}>
        <MDBox>
          <MDTypography variant="h4">Últimas Aféreses</MDTypography>
        </MDBox>
      </MDBox>
      <MDBox>
        {doacoesData && doacoesData.length > 0 ? (
          <DataTable
            entriesPerPage={{ defaultValue: 10 }}
            showTotalEntries={false}
            canSearch={true}
            isSorted={false}
            table={{
              columns: columns,
              rows: doacoesData,
            }}
          />
        ) : (
          <MDTypography>Nenhuma doação encontrada</MDTypography>
        )}
      </MDBox>
    </Card>
  );
}

export default LastDonations;
