import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PropTypes from "prop-types";
import { Card, Grid, Divider, Container, Box } from "@mui/material";
import Icon from "@mui/material/Icon";

// Components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDButton from "components/MDButton";
import ReveosIcon from "components/CustomIcons/Reveos";
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";

// Estilos para impressão
const printStyles = `
  @media print {
    @page {
      size: auto;
      margin: 15px;
    }

    body * {
      visibility: hidden;
      margin-top: -12px;
    }

    #report-content, #report-content * {
      visibility: visible;
    }

    #report-content {
      position: absolute;
      left: 0;
      top: 0;
      width: 95%;
      padding: 20px;
      margin: 0 auto;
    }

    .MuiContainer-root {
      max-width: 100% !important;
      padding: 0 !important;
    }

    .no-print {
      display: none !important;
    }
  }
`;

const ReportSection = ({ title, children }) => (
  <Box mb={3}>
    <MDTypography variant="h6" fontWeight="medium" mb={1}>
      {title}
    </MDTypography>
    <Box ml={2}>{children}</Box>
  </Box>
);

ReportSection.propTypes = {
  title: PropTypes.string.isRequired,
  children: PropTypes.node.isRequired,
};

const ReportField = ({ label, value }) => (
  <Grid item xs={6} sm={4} md={3}>
    <Box display="flex" flexDirection="column" mb={2}>
      <MDTypography variant="caption" color="text" fontWeight="bold">
        {label}
      </MDTypography>
      <MDTypography variant="body2">{value || "---"}</MDTypography>
    </Box>
  </Grid>
);

ReportField.propTypes = {
  label: PropTypes.string.isRequired,
  value: PropTypes.oneOfType([PropTypes.string, PropTypes.number, PropTypes.node]),
};

ReportField.defaultProps = {
  value: "---",
};

function ReveosReport() {
  const { codigo } = useParams();
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const style = document.createElement("style");
    style.innerHTML = printStyles;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  useEffect(() => {
    const fetchReportData = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem("authToken");

        const reportResponse = await fetch(
          `${process.env.REACT_APP_API_URL}/api/reveosReport/${codigo}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (!reportResponse.ok) {
          if (reportResponse.status === 404) {
            throw new Error("Relatório não encontrado");
          }
          throw new Error(`HTTP error! status: ${reportResponse.status}`);
        }

        const data = await reportResponse.json();
        setReportData(data);
        setError(null);
      } catch (error) {
        console.error("Erro ao buscar dados do relatório:", error);
        setError("Erro ao carregar os dados do relatório");
      } finally {
        setLoading(false);
      }
    };

    fetchReportData();
  }, [codigo]);

  const handlePrint = () => {
    window.print();
  };

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Card>
          <MDBox p={3}>
            {loading && (
              <MDTypography variant="h6" textAlign="center">
                Carregando...
              </MDTypography>
            )}

            {error && (
              <MDTypography variant="h6" color="error" textAlign="center">
                {error}
              </MDTypography>
            )}

            {!loading && !error && reportData && (
              <>
                <Box display="flex" justifyContent="flex-end" mb={2} className="no-print">
                  <MDButton
                    variant="gradient"
                    color="info"
                    onClick={handlePrint}
                    startIcon={<Icon>print</Icon>}
                  >
                    Imprimir Relatório
                  </MDButton>
                </Box>

                <Box id="report-content">
                  {/* Cabeçalho */}
                  <Box display="flex" alignItems="center" mb={3}>
                    <ReveosIcon sx={{ fontSize: 40, mr: 2 }} />
                    <Box>
                      <MDTypography variant="h4">
                        Relatório de Processamento de Unidade - {reportData.UnitNumber}
                      </MDTypography>
                    </Box>
                  </Box>

                  <Divider sx={{ my: 3 }} />

                  {/* Informações do Procedimento */}
                  <ReportSection title="Informações do Procedimento">
                    <Grid container spacing={2}>
                      <ReportField label="Data" value={reportData.StartDate} />
                      <ReportField label="Hora Início" value={reportData.StartDate} />
                      <ReportField label="Hora Fim" value={reportData.HoraFim} />
                      <ReportField label="Duração" value={reportData.Duracao} />
                    </Grid>
                  </ReportSection>

                  {/* Volumes Coletados */}
                  <ReportSection title="Volumes dos Componentes">
                    <Grid container spacing={2}>
                      <ReportField
                        label="Volume de Plaquetas"
                        value={
                          reportData.PlateletVolume ? `${reportData.PlateletVolume} mL` : "---"
                        }
                      />
                      <ReportField
                        label="Volume de Plasma"
                        value={reportData.PlasmaVolume ? `${reportData.PlasmaVolume} mL` : "---"}
                      />
                      <ReportField
                        label="Volume de Leucopack"
                        value={
                          reportData.LeukocyteVolume ? `${reportData.LeukocyteVolume} mL` : "---"
                        }
                      />
                    </Grid>
                  </ReportSection>

                  {/* Informações do Equipamento */}
                  <ReportSection title="Informações do Equipamento">
                    <Grid container spacing={2}>
                      <ReportField label="Número de Série" value={reportData.NumeroSerie} />
                      <ReportField label="Kit Utilizado" value={reportData.KitUtilizado} />
                      <ReportField label="Operador" value={reportData.Operador} />
                    </Grid>
                  </ReportSection>
                </Box>
              </>
            )}
          </MDBox>
        </Card>
      </Container>
    </DashboardLayout>
  );
}

export default ReveosReport;
