import React from "react";
import { Route, Navigate } from "react-router-dom";
import PropTypes from "prop-types";

const ProtectedRoute = ({ element, ...rest }) => {
  const isAuthenticated = !!localStorage.getItem("token"); // Verifica se o token está presente

  return (
    <Route
      {...rest}
      element={isAuthenticated ? element : <Navigate to="/authentication/sign-in" />}
    />
  );
};

// Definindo as propriedades esperadas
ProtectedRoute.propTypes = {
  element: PropTypes.element.isRequired, // 'element' é obrigatório e deve ser um elemento React
};

export default ProtectedRoute;
