import { useState } from "react";

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MenuItem from "@mui/material/MenuItem";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Divider from "@mui/material/Divider";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDButton from "components/MDButton";
import MDInput from "components/MDInput";

// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";

function Notifications() {
  const [email, setEmail] = useState("");
  const [frequency, setFrequency] = useState("daily");
  const [trimaIndicators, setTrimaIndicators] = useState({
    totalDonations: false,
    plateletCount: false,
    hemoglobinLevels: false,
    procedureDuration: false,
    alarmFrequency: false,
    donorReactions: false,
    collectionEfficiency: false,
    productQuality: false,
    equipmentUptime: false,
    successRate: false,
  });
  const [reveosIndicators, setReveosIndicators] = useState({
    processedComponents: false,
    processingTime: false,
    plateletYield: false,
    plasmaVolume: false,
    productQuality: false,
    equipmentAlarms: false,
    processingEfficiency: false,
    componentSeparation: false,
    wastageRate: false,
    batchCompletion: false,
  });

  const handleTrimaChange = (indicator) => {
    setTrimaIndicators({
      ...trimaIndicators,
      [indicator]: !trimaIndicators[indicator],
    });
  };

  const handleReveosChange = (indicator) => {
    setReveosIndicators({
      ...reveosIndicators,
      [indicator]: !reveosIndicators[indicator],
    });
  };

  const handleSave = () => {
    try {
      const notificationData = {
        email,
        frequency,
        trimaIndicators,
        reveosIndicators,
        lastUpdated: new Date().toISOString(),
      };

      // Salvar no localStorage
      localStorage.setItem("notificationSettings", JSON.stringify(notificationData));

      // Feedback visual para o usuário
      alert("Configurações salvas com sucesso!");
    } catch (error) {
      console.error("Erro ao salvar configurações:", error);
      alert("Erro ao salvar configurações. Por favor, tente novamente.");
    }
  };

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox py={3}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Card>
              <MDBox p={3}>
                <MDTypography variant="h5" mb={3}>
                  Configuração de Notificações
                </MDTypography>

                <Grid container spacing={3}>
                  <Grid item xs={12} md={8}>
                    <MDInput
                      type="email"
                      label="Email para notificações"
                      fullWidth
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <MDInput
                      select
                      label="Frequência"
                      value={frequency}
                      onChange={(e) => setFrequency(e.target.value)}
                      fullWidth
                      sx={{
                        "& .MuiSelect-select": {
                          padding: "0.75rem",
                          minHeight: "1.4375em",
                          height: "1.4375em",
                          display: "flex",
                          alignItems: "center",
                        },
                        "& .MuiInputBase-input": {
                          height: "1.4375em",
                        },
                        "& .MuiOutlinedInput-root": {
                          paddingTop: "0.75rem",
                          paddingBottom: "0.75rem",
                        },
                      }}
                    >
                      <MenuItem value="daily">Diária</MenuItem>
                      <MenuItem value="weekly">Semanal</MenuItem>
                      <MenuItem value="monthly">Mensal</MenuItem>
                    </MDInput>
                  </Grid>
                </Grid>

                <Divider sx={{ my: 3 }} />

                {/* Trima Indicators */}
                <MDTypography variant="h6" mb={2}>
                  Indicadores Trima
                </MDTypography>
                <Grid container spacing={2}>
                  {Object.entries(trimaIndicators).map(([key, value]) => (
                    <Grid item xs={12} sm={6} md={4} key={key}>
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={value}
                            onChange={() => handleTrimaChange(key)}
                            color="primary"
                          />
                        }
                        label={key
                          .replace(/([A-Z])/g, " $1")
                          .replace(/^./, (str) => str.toUpperCase())}
                      />
                    </Grid>
                  ))}
                </Grid>

                <Divider sx={{ my: 3 }} />

                {/* Reveos Indicators */}
                <MDTypography variant="h6" mb={2}>
                  Indicadores Reveos
                </MDTypography>
                <Grid container spacing={2}>
                  {Object.entries(reveosIndicators).map(([key, value]) => (
                    <Grid item xs={12} sm={6} md={4} key={key}>
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={value}
                            onChange={() => handleReveosChange(key)}
                            color="primary"
                          />
                        }
                        label={key
                          .replace(/([A-Z])/g, " $1")
                          .replace(/^./, (str) => str.toUpperCase())}
                      />
                    </Grid>
                  ))}
                </Grid>

                <MDBox mt={4} display="flex" justifyContent="flex-end">
                  <MDButton variant="gradient" color="info" onClick={handleSave}>
                    Salvar Configurações
                  </MDButton>
                </MDBox>
              </MDBox>
            </Card>
          </Grid>
        </Grid>
      </MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Notifications;
