import { useState, useEffect } from "react";

// @mui material components
import Card from "@mui/material/Card";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";

// Data
import DataTable from "examples/Tables/DataTable";

function Productivity() {
  const [produtividadeData, setProdutividadeData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      console.log("Iniciando busca de dados de produtividade...");
      try {
        const token = localStorage.getItem("authToken");
        console.log("Token encontrado:", token ? "Sim" : "Não");

        if (!token) {
          throw new Error("Nenhum token de autenticação encontrado");
        }

        const apiUrl = `${process.env.REACT_APP_API_URL || 'http://localhost:8001'}/api/produtividadeUsuarios`;
        console.log("Fazendo requisição para:", apiUrl);

        const response = await fetch(apiUrl, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        console.log("Status da resposta:", response.status);

        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            console.log("Erro de autenticação, redirecionando para login");
            localStorage.removeItem("authToken");
            window.location.href = "/authentication/sign-in";
            return;
          }
          throw new Error(`Erro HTTP: ${response.status}`);
        }

        const data = await response.json();
        console.log("Dados recebidos:", data);

        const dados = data.map((usuario) => ({
          name: `${usuario.FIRST_NAME} ${usuario.LAST_NAME}`,
          aferese: usuario.TotalAferese.toString(),
          st: usuario.TotalReveos,
        }));

        console.log("Dados processados:", dados);
        setProdutividadeData(dados);
        setIsLoading(false);
      } catch (error) {
        console.error("Erro detalhado:", error);
        setError(error.message);
        setProdutividadeData([]);
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  if (isLoading) {
    return (
      <Card>
        <MDBox p={3}>
          <MDTypography variant="h6">Carregando dados...</MDTypography>
        </MDBox>
      </Card>
    );
  }

  if (error) {
    return (
      <Card>
        <MDBox p={3}>
          <MDTypography variant="h6" color="error">
            Erro ao carregar dados: {error}
          </MDTypography>
        </MDBox>
      </Card>
    );
  }

  return (
    <Card>
      <MDBox display="flex" justifyContent="space-between" alignItems="center" p={3}>
        <MDBox>
          <MDTypography variant="h6">Produtividade por usuário</MDTypography>
          <MDTypography variant="button" color="text">
            {produtividadeData.length} usuários encontrados
          </MDTypography>
        </MDBox>
      </MDBox>
      <MDBox>
        <DataTable
          showTotalEntries={true}
          canSearch={true}
          table={{
            columns: [
              { Header: "Nome", accessor: "name", width: "30%" },
              { Header: "Aférese", accessor: "aferese", width: "30%" },
              { Header: "Sangue Total", accessor: "st", width: "30%" },
            ],
            rows: produtividadeData,
          }}
        />
      </MDBox>
    </Card>
  );
}

export default Productivity;
