import React, { useState, useEffect } from "react";

// @mui material components
import Card from "@mui/material/Card";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";

function UncollectedComponents() {
  const [data, setData] = useState({
    totalUncollected: 0,
    percentage: 0,
    trend: "up",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("authToken");
        if (!token) {
          throw new Error("Nenhum token de autenticação encontrado");
        }

        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/apheresisComponents`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            localStorage.removeItem("authToken");
            window.location.href = "/authentication/sign-in";
            return;
          }
          throw new Error(`Erro HTTP: ${response.status}`);
        }

        const dados = await response.json();

        // Ordenar dados por ano e mês
        const dadosOrdenados = dados.sort((a, b) => {
          if (a.Ano !== b.Ano) return a.Ano - b.Ano;
          return a.Mes - b.Mes;
        });

        // Calcular total não coletado
        const totalOffered = dadosOrdenados.reduce(
          (acc, item) => acc + (item.TotalOffered || 0),
          0
        );
        const totalCollected = dadosOrdenados.reduce(
          (acc, item) => acc + (item.TotalCollected || 0),
          0
        );
        const totalUncollected = totalOffered - totalCollected;

        // Calcular porcentagem
        const percentage = totalOffered > 0 ? (totalUncollected / totalOffered) * 100 : 0;

        // Determinar tendência comparando com o mês anterior
        const lastMonth = dadosOrdenados[dadosOrdenados.length - 1];
        const previousMonth = dadosOrdenados[dadosOrdenados.length - 2];
        const lastMonthUncollected =
          (lastMonth.TotalOffered || 0) - (lastMonth.TotalCollected || 0);
        const previousMonthUncollected =
          (previousMonth.TotalOffered || 0) - (previousMonth.TotalCollected || 0);
        const trend = lastMonthUncollected > previousMonthUncollected ? "up" : "down";

        setData({
          totalUncollected,
          percentage: Math.round(percentage),
          trend,
        });
      } catch (error) {
        console.error("Erro ao buscar dados:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <Card sx={{ height: "100%" }}>
      <MDBox p={3}>
        <MDBox>
          <MDTypography variant="h6" fontWeight="medium" color="text">
            Componentes Oferecidos e não Coletados
          </MDTypography>
          <MDTypography variant="button" color="text" fontWeight="regular">
            Diferença entre oferecidos e coletados nos últimos 6 meses
          </MDTypography>
        </MDBox>
        <MDBox mt={2}>
          <MDTypography variant="h4" fontWeight="bold" color="error">
            {data.totalUncollected}
          </MDTypography>
          <MDBox display="flex" alignItems="center" mt={1}>
            <MDTypography variant="button" color="error" fontWeight="medium">
              {data.percentage}% dos componentes oferecidos
            </MDTypography>
          </MDBox>
        </MDBox>
      </MDBox>
    </Card>
  );
}

export default UncollectedComponents;
