// Material Dashboard 2 React layouts
import Dashboard from "layouts/dashboard";
import { useNavigate } from "react-router-dom"; // Importar useNavigate
import Icon from "@mui/material/Icon";

// Pages
import Trima from "layouts/trima";
import Example from "layouts/tables";

// Examples
import Tables from "layouts/tables";
import Profile from "layouts/profile";
import Notifications from "layouts/notifications";
import SignIn from "layouts/authentication/sign-in";

// @mui icons
import NotificationsActiveIcon from "@mui/icons-material/NotificationsActive";

// Custom Icons
import ReveosIcon from "components/CustomIcons/Reveos";
import TRACIcon from "components/CustomIcons/TRAC";
import TrimaIcon from "components/CustomIcons/Trima";
import ArchimedeIcon from "components/CustomIcons/Archimede";

// Componente inline para Logout
const LogoutItem = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("authToken"); // Destrói o token
    navigate("/authentication/sign-in", { replace: true }); // Redireciona
  };

  return (
    <Icon fontSize="small" onClick={handleLogout}>
      logout
    </Icon>
  );
};

const routes = [
  {
    type: "collapse",
    name: "Dashboard",
    key: "dashboard",
    icon: <Icon fontSize="small">dashboard</Icon>,
    route: "/dashboard",
    component: <Dashboard />,
  },
  {
    type: "collapse",
    name: "Trima",
    key: "trima",
    icon: <TrimaIcon />,
    route: "/dashboard/trima",
    component: <Trima />,
  },
  {
    type: "collapse",
    name: "Reveos",
    key: "reveos",
    icon: <ReveosIcon />,
    route: "/rlt",
    component: <Example />,
  },
  {
    type: "collapse",
    name: "T-RAC",
    key: "trac",
    icon: <TRACIcon />,
    route: "/dashboard",
    component: <Trima />,
  },
  {
    type: "collapse",
    name: "Archimede",
    key: "archimede",
    icon: <ArchimedeIcon />,
    route: "/dashboard",
    component: <Trima />,
  },
  {
    type: "collapse",
    name: "Notificações",
    key: "notificacoes",
    icon: <NotificationsActiveIcon />,
    route: "/dashboard",
    component: <Trima />,
  },
  {
    type: "collapse",
    name: "Sign In",
    key: "sign-in",
    icon: <Icon fontSize="small">login</Icon>,
    route: "/authentication/sign-in",
    component: <SignIn />,
  },
  {
    type: "collapse",
    name: "Logout",
    key: "logout",
    icon: <LogoutItem />, // Usa o componente inline
  },
];

export default routes;
