import { useState } from "react";
import { useNavigate } from "react-router-dom";
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDInput from "components/MDInput";
import MDButton from "components/MDButton";

function SignIn() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8001/api/auth/signin", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem("authToken", data.token);
        navigate("/dashboard");
      } else {
        setError(data.error || "Erro ao fazer login");
      }
    } catch (err) {
      setError("Erro de conexão com o servidor");
      console.error(err);
    }
  };

  return (
    <MDBox pt={6} pb={3} mx="auto" maxWidth="400px">
      <MDBox
        component="form"
        onSubmit={handleSubmit}
        p={3}
        bgColor="white"
        borderRadius="lg"
        shadow="md"
      >
        <MDTypography variant="h4" mb={3} textAlign="center">
          Login
        </MDTypography>
        <MDBox mb={2}>
          <MDInput
            type="text"
            label="Usuário"
            fullWidth
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </MDBox>
        <MDBox mb={2}>
          <MDInput
            type="password"
            label="Senha"
            fullWidth
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </MDBox>
        {error && (
          <MDTypography color="error" variant="body2" mb={2} textAlign="center">
            {error}
          </MDTypography>
        )}
        <MDButton type="submit" variant="gradient" color="dark" fullWidth>
          Entrar
        </MDButton>
      </MDBox>
    </MDBox>
  );
}

export default SignIn;
