import { useState, useEffect } from "react";
import PropTypes from "prop-types";

// @mui material components
import Card from "@mui/material/Card";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";

// Data
import DataTable from "examples/Tables/DataTable";

// Componente para célula personalizada
const CustomCell = ({ value }) => (
  <MDTypography
    component="a"
    href="#"
    onClick={(e) => {
      e.preventDefault();
      value.onClick();
    }}
    variant="caption"
    color="text"
    fontWeight="medium"
    sx={{ cursor: "pointer" }}
  >
    {value.text}
  </MDTypography>
);

// Definindo PropTypes para o componente CustomCell
CustomCell.propTypes = {
  value: PropTypes.shape({
    text: PropTypes.string.isRequired,
    onClick: PropTypes.func.isRequired,
  }).isRequired,
};

function LastDonations() {
  const [doacoesData, setDoacoesData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("authToken");
        if (!token) {
          throw new Error("Nenhum token de autenticação encontrado");
        }

        const response = await fetch("http://localhost:8001/api/apheresisProcedures", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        const dados = data.map((doacao) => {
          const dataProcedimento = new Date(doacao.DataProcedimento);
          return {
            codigo: {
              text: doacao.CodigoDoacao,
              onClick: () => (window.location.href = `/trima/${doacao.CodigoDoacao}`),
            },
            data: {
              text: dataProcedimento.toLocaleDateString("pt-BR", {
                day: "2-digit",
                month: "2-digit",
                year: "2-digit",
              }),
              onClick: () => (window.location.href = `/trima/${doacao.CodigoDoacao}`),
            },
            hora: {
              text: dataProcedimento.toLocaleTimeString("pt-BR", {
                hour: "2-digit",
                minute: "2-digit",
                hour12: false,
              }),
              onClick: () => (window.location.href = `/trima/${doacao.CodigoDoacao}`),
            },
          };
        });
        setDoacoesData(dados);
      } catch (error) {
        console.error("Erro ao buscar dados:", error);
      }
    };

    fetchData();
  }, []);

  const columns = [
    {
      Header: "Código doação",
      accessor: "codigo",
      width: "34%",
      Cell: CustomCell,
    },
    {
      Header: "Data",
      accessor: "data",
      width: "33%",
      Cell: CustomCell,
    },
    {
      Header: "Hora Inicio",
      accessor: "hora",
      width: "33%",
      Cell: CustomCell,
    },
  ];

  return (
    <Card>
      <MDBox display="flex" justifyContent="space-between" alignItems="center" p={3}>
        <MDBox>
          <MDTypography variant="h4">Últimas Aféreses</MDTypography>
        </MDBox>
      </MDBox>
      <MDBox>
        {doacoesData && doacoesData.length > 0 ? (
          <DataTable
            entriesPerPage={{ defaultValue: 10 }}
            showTotalEntries={false}
            canSearch={true}
            isSorted={false}
            table={{
              columns: columns,
              rows: doacoesData,
            }}
          />
        ) : (
          <MDTypography>Nenhuma doação encontrada</MDTypography>
        )}
      </MDBox>
    </Card>
  );
}

export default LastDonations;
