import { useState, useEffect } from "react";

const useAlarmFrequencyData = () => {
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: "Frequência",
        color: "error",
        data: [],
      },
    ],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("authToken");
        const apiUrl = process.env.REACT_APP_API_URL;

        if (!token) {
          throw new Error("Nenhum token de autenticação encontrado");
        }

        const response = await fetch(`${apiUrl}/api/topAlarms`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            localStorage.removeItem("authToken");
            window.location.href = "/authentication/sign-in";
            return;
          }
          throw new Error(`Erro HTTP: ${response.status}`);
        }

        const data = await response.json();

        // Formatar os labels para mostrar o ID e uma versão truncada da mensagem
        const formatLabel = (alarmId, message) => {
          const shortMessage = message.split(" ").slice(1).join(" "); // Remove o ID do início
          return `${alarmId}: ${shortMessage.substring(0, 30)}${
            shortMessage.length > 30 ? "..." : ""
          }`;
        };

        setChartData({
          labels: data.map((item) => formatLabel(item.AlarmId, item.Message)),
          datasets: [
            {
              label: "Frequência de Alarmes",
              color: "error",
              data: data.map((item) => item.Frequencia),
            },
          ],
        });
      } catch (error) {
        console.error("Erro ao buscar dados:", error);
        setChartData({
          labels: ["Sem dados"],
          datasets: [
            {
              label: "Frequência de Alarmes",
              color: "error",
              data: [0],
            },
          ],
        });
      }
    };

    fetchData();
  }, []);

  return chartData;
};

export default useAlarmFrequencyData;
