import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import axios from "axios";

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MenuItem from "@mui/material/MenuItem";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Divider from "@mui/material/Divider";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import Snackbar from "@mui/material/Snackbar";
import Alert from "@mui/material/Alert";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDButton from "components/MDButton";
import MDInput from "components/MDInput";

// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`indicator-tabpanel-${index}`}
      aria-labelledby={`indicator-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

// Adiciona PropTypes para TabPanel
TabPanel.propTypes = {
  children: PropTypes.node.isRequired,
  value: PropTypes.number.isRequired,
  index: PropTypes.number.isRequired,
};

function Notifications() {
  const [activeStep, setActiveStep] = useState(0);
  const [selectedTab, setSelectedTab] = useState(0);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarSeverity, setSnackbarSeverity] = useState("success");

  const [email, setEmail] = useState("");
  const [frequency, setFrequency] = useState("weekly");

  // Dashboard indicators
  const [dashboardIndicators, setDashboardIndicators] = useState({
    doacoesPorAfereses: false,
    doacoesPorSangueTotal: false,
    componentesProduzidos: false,
    produtividade: false,
  });

  // Trima indicators
  const [trimaIndicators, setTrimaIndicators] = useState({
    doacoesPorAfereses: false,
    plaquetasOfertadasColetadas: false,
    plaquetasPreDoador: false,
    htHbPreDoador: false,
    duracaoProcedimentos: false,
    top10Alarmes: false,
  });

  // Reveos indicators
  const [reveosIndicators, setReveosIndicators] = useState({
    componentesProcessados: false,
    duracao: false,
    volumeMedioPlaquetas: false,
    pyiPlaquetas: false,
    volumeMedioPlasma: false,
    volumeTotalPlasma: false,
    top10Alarmes: false,
  });

  useEffect(() => {
    // Verificar se o usuário está autenticado
    const authToken = localStorage.getItem("authToken");
    const username = localStorage.getItem("username");

    if (!authToken || !username) {
      console.error("Usuário não autenticado");
      showSnackbar("É necessário estar autenticado para configurar notificações", "error");
      return;
    }

    // Carregar configurações salvas do arquivo
    loadNotificationSettings();
  }, []);

  // Função para migrar configurações antigas para o novo formato (compatibilidade)
  const migrateOldSettings = (oldSettings) => {
    const newDashboard = {};
    const newTrima = {};
    const newReveos = {};

    // Mapear Dashboard indicators
    if (oldSettings.dashboardIndicators) {
      const oldDashboard = oldSettings.dashboardIndicators;
      newDashboard.doacoesPorAfereses =
        oldDashboard.totalDonationsAferese ||
        oldDashboard.totalDoacoesAferese ||
        oldDashboard.doacoesAferese ||
        false;
      newDashboard.doacoesPorSangueTotal =
        oldDashboard.totalDonationsSangueTotal ||
        oldDashboard.totalDoacoesSangueTotal ||
        oldDashboard.doacoesSangueTotal ||
        false;
      newDashboard.componentesProduzidos =
        oldDashboard.totalComponentesProduzidos || oldDashboard.componentesProduzidos || false;
      newDashboard.produtividade =
        oldDashboard.produtividadeGeral ||
        oldDashboard.produtividadePorUsuario ||
        oldDashboard.produtividadeUsuario ||
        oldDashboard.produtividade ||
        false;
    }

    // Mapear Trima indicators
    if (oldSettings.trimaIndicators) {
      const oldTrima = oldSettings.trimaIndicators;
      newTrima.doacoesPorAfereses =
        oldTrima.totalDonations ||
        oldTrima.totalDoacoes ||
        oldTrima.doacoesAferese ||
        oldTrima.doacoesPorAfereses ||
        false;
      newTrima.plaquetasOfertadasColetadas =
        oldTrima.plateletOfferedVsCollected ||
        oldTrima.totalOfertadoXColetado ||
        oldTrima.plaquetasOfertadasXColetadas ||
        oldTrima.plaquetasOfertadasColetadas ||
        false;
      newTrima.plaquetasPreDoador = oldTrima.plateletCount || oldTrima.plaquetasPreDoador || false;
      newTrima.htHbPreDoador = oldTrima.hemoglobinLevels || oldTrima.htHbPreDoador || false;
      newTrima.duracaoProcedimentos =
        oldTrima.procedureDuration || oldTrima.duracaoProcedimentos || false;
      newTrima.top10Alarmes = oldTrima.alarmFrequency || oldTrima.top10Alarmes || false;
    }

    // Mapear Reveos indicators
    if (oldSettings.reveosIndicators) {
      const oldReveos = oldSettings.reveosIndicators;
      newReveos.componentesProcessados =
        oldReveos.processedComponents ||
        oldReveos.totalComponentesProcessados ||
        oldReveos.componentesProcessados ||
        false;
      newReveos.duracao = oldReveos.processingTime || oldReveos.duracao || false;
      newReveos.volumeMedioPlaquetas =
        oldReveos.plateletVolume || oldReveos.volumeMedioPlaquetas || false;
      newReveos.pyiPlaquetas =
        oldReveos.plateletYield || oldReveos.plateletIndex || oldReveos.pyiPlaquetas || false;
      newReveos.volumeMedioPlasma = oldReveos.plasmaVolume || oldReveos.volumeMedioPlasma || false;
      newReveos.volumeTotalPlasma =
        oldReveos.totalPlasmaVolume || oldReveos.volumeTotalPlasma || false;
      newReveos.top10Alarmes = oldReveos.equipmentAlarms || oldReveos.top10Alarmes || false;
    }

    return {
      dashboardIndicators: newDashboard,
      trimaIndicators: newTrima,
      reveosIndicators: newReveos,
      email: oldSettings.email || "",
      frequency: oldSettings.frequency || "weekly",
      lastUpdated: new Date().toISOString(),
      userId: oldSettings.userId || localStorage.getItem("userId") || "guest",
    };
  };

  // Função para carregar as configurações de notificações do arquivo
  const loadNotificationSettings = async () => {
    try {
      console.log("Carregando configurações do servidor...");

      // Obter o nome de usuário do localStorage
      const username = localStorage.getItem("username");

      if (!username) {
        console.warn("Nome de usuário não encontrado no localStorage");
        showSnackbar("Erro ao carregar configurações: Usuário não identificado", "warning");
        return;
      }

      // Carregar do servidor (arquivo persistente)
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/notifications/settings`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
          },
        }
      );

      if (response.status === 200 && response.data) {
        // Se encontrou configurações no servidor
        const oldSettings = response.data;
        // Migrar configurações antigas para o novo formato
        const settings = migrateOldSettings(oldSettings);

        setEmail(settings.email || "");
        setFrequency(settings.frequency || "weekly");
        setDashboardIndicators(settings.dashboardIndicators);
        setTrimaIndicators(settings.trimaIndicators);
        setReveosIndicators(settings.reveosIndicators);

        // Atualizar também no localStorage para fallback
        localStorage.setItem("notificationSettings", JSON.stringify(settings));

        console.log("Configurações carregadas com sucesso do servidor");
      } else {
        // Se não encontrou configurações no servidor
        console.log("Nenhuma configuração encontrada no servidor. Verificando localStorage...");

        // Verificar se já existem configurações no localStorage como fallback
        const localSettings = localStorage.getItem("notificationSettings");

        if (localSettings) {
          try {
            const oldSettings = JSON.parse(localSettings);
            // Migrar configurações antigas para o novo formato
            const settings = migrateOldSettings(oldSettings);

            setEmail(settings.email || "");
            setFrequency(settings.frequency || "weekly");
            setDashboardIndicators(settings.dashboardIndicators);
            setTrimaIndicators(settings.trimaIndicators);
            setReveosIndicators(settings.reveosIndicators);

            console.log("Configurações carregadas do localStorage");

            // Salvar as configurações do localStorage no servidor
            saveToServer(settings);
          } catch (error) {
            console.error("Erro ao processar configurações locais:", error);
            showSnackbar("Erro ao carregar configurações salvas localmente", "error");
          }
        }
      }
    } catch (error) {
      console.error("Erro ao carregar configurações do servidor:", error);
      showSnackbar(`Erro ao carregar configurações: ${error.message}`, "error");

      // Em caso de erro, tenta usar o localStorage como fallback
      const localSettings = localStorage.getItem("notificationSettings");
      if (localSettings) {
        try {
          const oldSettings = JSON.parse(localSettings);
          // Migrar configurações antigas para o novo formato
          const settings = migrateOldSettings(oldSettings);

          setEmail(settings.email || "");
          setFrequency(settings.frequency || "weekly");
          setDashboardIndicators(settings.dashboardIndicators);
          setTrimaIndicators(settings.trimaIndicators);
          setReveosIndicators(settings.reveosIndicators);

          console.log("Usando configurações do localStorage como fallback");
        } catch (parseError) {
          console.error("Erro ao processar configurações locais:", parseError);
        }
      }
    }
  };

  // Função para salvar as configurações no servidor (arquivo persistente)
  const saveToServer = async (notificationData, retryCount = 0) => {
    try {
      console.log("Salvando configurações no servidor...");

      // Obter o nome de usuário do localStorage
      const username = localStorage.getItem("username");

      if (!username) {
        console.warn("Nome de usuário não encontrado no localStorage");
        showSnackbar("Erro ao salvar: Usuário não identificado", "warning");
        return false;
      }

      // Adicionar o nome de usuário aos dados
      notificationData.username = username;
      notificationData.userId = localStorage.getItem("userId") || "guest";

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/notifications/settings`,
        notificationData,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
          },
          timeout: 5000, // Timeout de 5 segundos
        }
      );

      if (response.status !== 200 && response.status !== 201) {
        throw new Error(`Erro ao salvar no servidor: ${response.statusText}`);
      }

      console.log("Configurações salvas com sucesso no servidor");
      return true;
    } catch (error) {
      console.error("Erro ao salvar configurações no servidor:", error);

      // Se houver falha de conectividade ou timeout, tentar novamente (até 2 vezes)
      if (
        retryCount < 2 &&
        (error.code === "ECONNABORTED" ||
          error.message.includes("timeout") ||
          error.message.includes("Network Error"))
      ) {
        console.log(`Tentando novamente (${retryCount + 1} de 2)...`);
        return new Promise((resolve) => {
          setTimeout(async () => {
            const result = await saveToServer(notificationData, retryCount + 1);
            resolve(result);
          }, 1000); // Aguardar 1 segundo antes de tentar novamente
        });
      }

      showSnackbar(
        `Erro ao salvar no servidor: ${error.message}. As configurações foram salvas localmente.`,
        "warning"
      );
      return false;
    }
  };

  const showSnackbar = (message, severity = "success") => {
    setSnackbarMessage(message);
    setSnackbarSeverity(severity);
    setSnackbarOpen(true);
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  const handleTestEmail = async () => {
    if (!email) {
      showSnackbar("Por favor, informe um email válido", "warning");
      return;
    }

    try {
      showSnackbar("Enviando email de teste...", "info");

      // Obter o nome de usuário do localStorage
      const username = localStorage.getItem("username");

      if (!username) {
        showSnackbar("Erro ao enviar email: Usuário não identificado", "warning");
        return;
      }

      // Preparar dados dos indicadores selecionados
      const selectedIndicators = {
        dashboardIndicators: dashboardIndicators,
        trimaIndicators: trimaIndicators,
        reveosIndicators: reveosIndicators,
      };

      // Verificar se pelo menos um indicador foi selecionado
      const hasSelectedIndicator =
        Object.values(dashboardIndicators).some((value) => value) ||
        Object.values(trimaIndicators).some((value) => value) ||
        Object.values(reveosIndicators).some((value) => value);

      // Tentar enviar o email de teste
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/notifications/test-email`,
        {
          email,
          frequency,
          indicators: hasSelectedIndicator ? selectedIndicators : null,
          username: username,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
          },
          timeout: 10000, // 10 segundos de timeout
        }
      );

      if (response.status === 200) {
        showSnackbar(
          hasSelectedIndicator
            ? "Email de teste com dados reais enviado com sucesso! Verifique sua caixa de entrada."
            : "Email de teste enviado com sucesso! Verifique sua caixa de entrada.",
          "success"
        );
      } else {
        throw new Error("Erro ao enviar email de teste");
      }
    } catch (error) {
      console.error("Erro ao enviar email de teste:", error);

      // Formatar mensagem de erro mais amigável
      let errorMessage = "Falha ao enviar email de teste";

      if (error.response?.data?.details) {
        // Se o erro contém detalhes da API
        errorMessage += `: ${error.response.data.details}`;

        // Adicionar dicas específicas para erros comuns
        if (
          error.response.data.details.includes("Invalid login") ||
          error.response.data.details.includes("Username and Password not accepted")
        ) {
          errorMessage +=
            ". Verifique as configurações de email do servidor (autenticação em dois fatores ou senha de aplicativo)";
        }
      } else if (error.message) {
        // Erro genérico
        errorMessage += `: ${error.message}`;
      }

      showSnackbar(errorMessage, "error");
    }
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleDashboardChange = (indicator) => {
    setDashboardIndicators({
      ...dashboardIndicators,
      [indicator]: !dashboardIndicators[indicator],
    });
  };

  const handleTrimaChange = (indicator) => {
    setTrimaIndicators({
      ...trimaIndicators,
      [indicator]: !trimaIndicators[indicator],
    });
  };

  const handleReveosChange = (indicator) => {
    setReveosIndicators({
      ...reveosIndicators,
      [indicator]: !reveosIndicators[indicator],
    });
  };

  const selectAllDashboard = () => {
    const newState = {};
    Object.keys(dashboardIndicators).forEach((key) => {
      newState[key] = true;
    });
    setDashboardIndicators(newState);
  };

  const selectAllTrima = () => {
    const newState = {};
    Object.keys(trimaIndicators).forEach((key) => {
      newState[key] = true;
    });
    setTrimaIndicators(newState);
  };

  const selectAllReveos = () => {
    const newState = {};
    Object.keys(reveosIndicators).forEach((key) => {
      newState[key] = true;
    });
    setReveosIndicators(newState);
  };

  const deselectAllDashboard = () => {
    const newState = {};
    Object.keys(dashboardIndicators).forEach((key) => {
      newState[key] = false;
    });
    setDashboardIndicators(newState);
  };

  const deselectAllTrima = () => {
    const newState = {};
    Object.keys(trimaIndicators).forEach((key) => {
      newState[key] = false;
    });
    setTrimaIndicators(newState);
  };

  const deselectAllReveos = () => {
    const newState = {};
    Object.keys(reveosIndicators).forEach((key) => {
      newState[key] = false;
    });
    setReveosIndicators(newState);
  };

  const formatIndicatorLabel = (key) => {
    const labels = {
      // Dashboard
      doacoesPorAfereses: "Total de doações por Aféreses",
      doacoesPorSangueTotal: "Total de doações por Sangue Total",
      componentesProduzidos: "Total de Componentes Produzidos",
      produtividade: "Produtividade",

      // Trima
      plaquetasOfertadasColetadas: "Total de Plaquetas Oferecidas x Coletadas",
      plaquetasPreDoador: "Plaquetas Pré - Doador",
      htHbPreDoador: "Ht/Hb Pré - Doador",
      duracaoProcedimentos: "Duração dos procedimentos",
      top10Alarmes: "Top 10 Alarmes",

      // Reveos
      componentesProcessados: "Componentes processados",
      duracao: "Duração",
      volumeMedioPlaquetas: "Volume Médio Plaquetas",
      pyiPlaquetas: "PYI Plaquetas",
      volumeMedioPlasma: "Volume Médio de Plasma",
      volumeTotalPlasma: "Volume Total de Plasma",
    };
    return labels[key] || key;
  };

  const handleSave = async () => {
    try {
      // Verificar se pelo menos um indicador foi selecionado
      const hasSelectedIndicator =
        Object.values(dashboardIndicators).some((value) => value) ||
        Object.values(trimaIndicators).some((value) => value) ||
        Object.values(reveosIndicators).some((value) => value);

      if (!hasSelectedIndicator) {
        showSnackbar("Selecione pelo menos um indicador para continuar", "warning");
        return;
      }

      if (!email) {
        showSnackbar("Por favor, informe um email válido", "warning");
        return;
      }

      // Obter o nome de usuário do localStorage
      const username = localStorage.getItem("username");

      if (!username) {
        showSnackbar("Erro ao salvar: Usuário não identificado", "warning");
        return;
      }

      // Mostrar indicador de carregamento para o usuário
      showSnackbar("Salvando configurações...", "info");

      const notificationData = {
        email,
        frequency,
        dashboardIndicators,
        trimaIndicators,
        reveosIndicators,
        lastUpdated: new Date().toISOString(),
        userId: localStorage.getItem("userId") || "guest",
        username: username
      };

      // Salvar no localStorage como fallback
      localStorage.setItem("notificationSettings", JSON.stringify(notificationData));
      console.log("Configurações salvas localmente no localStorage");

      // Salvar no servidor (arquivo persistente)
      const serverSaved = await saveToServer(notificationData);

      // Feedback visual para o usuário
      if (serverSaved) {
        showSnackbar("Configurações salvas com sucesso!");
      } else {
        // Verificar conexão com o servidor
        try {
          await axios.get(`${process.env.REACT_APP_API_URL}/api/health`, {
            timeout: 3000,
          });
          // Se conseguir conectar ao servidor mas falhou ao salvar
          showSnackbar(
            "Configurações salvas localmente, mas houve um problema específico ao salvar no servidor. Tente novamente mais tarde.",
            "warning"
          );
        } catch (connectionError) {
          // Se não conseguir conectar ao servidor
          showSnackbar(
            "Configurações salvas localmente. Não foi possível conectar ao servidor. Verifique sua conexão.",
            "warning"
          );
        }
      }
    } catch (error) {
      console.error("Erro ao salvar configurações:", error);
      showSnackbar("Erro ao salvar configurações. Por favor, tente novamente.", "error");
    }
  };

  const countSelectedIndicators = () => {
    const dashboardCount = Object.values(dashboardIndicators).filter((v) => v).length;
    const trimaCount = Object.values(trimaIndicators).filter((v) => v).length;
    const reveosCount = Object.values(reveosIndicators).filter((v) => v).length;
    return dashboardCount + trimaCount + reveosCount;
  };

  const steps = ["Selecionar Indicadores", "Configurar Email", "Revisar e Salvar"];

  const renderIndicatorSelection = () => (
    <>
      <MDTypography variant="h6" gutterBottom>
        Selecione os indicadores para receber notificações
      </MDTypography>

      <MDTypography variant="body2" color="text.secondary" mb={2}>
        Selecione os indicadores que deseja monitorar através de notificações por email.
      </MDTypography>

      <Tabs
        value={selectedTab}
        onChange={handleTabChange}
        variant="fullWidth"
        sx={{ mb: 2, borderBottom: 1, borderColor: "divider" }}
      >
        <Tab label="Dashboard" />
        <Tab label="Trima" />
        <Tab label="Reveos" />
      </Tabs>

      {/* Dashboard Indicators */}
      <TabPanel value={selectedTab} index={0}>
        <MDBox display="flex" justifyContent="space-between" mb={2}>
          <MDTypography variant="subtitle1">
            Indicadores do Dashboard ({Object.values(dashboardIndicators).filter((v) => v).length}/
            {Object.keys(dashboardIndicators).length})
          </MDTypography>
          <MDBox>
            <Button size="small" onClick={selectAllDashboard} sx={{ mr: 1 }}>
              Selecionar Todos
            </Button>
            <Button size="small" onClick={deselectAllDashboard}>
              Limpar Seleção
            </Button>
          </MDBox>
        </MDBox>

        <Grid container spacing={2}>
          {Object.entries(dashboardIndicators).map(([key, value]) => (
            <Grid item xs={12} sm={6} md={4} key={key}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={value}
                    onChange={() => handleDashboardChange(key)}
                    color="primary"
                  />
                }
                label={formatIndicatorLabel(key)}
              />
            </Grid>
          ))}
        </Grid>
      </TabPanel>

      {/* Trima Indicators */}
      <TabPanel value={selectedTab} index={1}>
        <MDBox display="flex" justifyContent="space-between" mb={2}>
          <MDTypography variant="subtitle1">
            Indicadores Trima ({Object.values(trimaIndicators).filter((v) => v).length}/
            {Object.keys(trimaIndicators).length})
          </MDTypography>
          <MDBox>
            <Button size="small" onClick={selectAllTrima} sx={{ mr: 1 }}>
              Selecionar Todos
            </Button>
            <Button size="small" onClick={deselectAllTrima}>
              Limpar Seleção
            </Button>
          </MDBox>
        </MDBox>

        <Grid container spacing={2}>
          {Object.entries(trimaIndicators).map(([key, value]) => (
            <Grid item xs={12} sm={6} md={4} key={key}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={value}
                    onChange={() => handleTrimaChange(key)}
                    color="primary"
                  />
                }
                label={formatIndicatorLabel(key)}
              />
            </Grid>
          ))}
        </Grid>
      </TabPanel>

      {/* Reveos Indicators */}
      <TabPanel value={selectedTab} index={2}>
        <MDBox display="flex" justifyContent="space-between" mb={2}>
          <MDTypography variant="subtitle1">
            Indicadores Reveos ({Object.values(reveosIndicators).filter((v) => v).length}/
            {Object.keys(reveosIndicators).length})
          </MDTypography>
          <MDBox>
            <Button size="small" onClick={selectAllReveos} sx={{ mr: 1 }}>
              Selecionar Todos
            </Button>
            <Button size="small" onClick={deselectAllReveos}>
              Limpar Seleção
            </Button>
          </MDBox>
        </MDBox>

        <Grid container spacing={2}>
          {Object.entries(reveosIndicators).map(([key, value]) => (
            <Grid item xs={12} sm={6} md={4} key={key}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={value}
                    onChange={() => handleReveosChange(key)}
                    color="primary"
                  />
                }
                label={formatIndicatorLabel(key)}
              />
            </Grid>
          ))}
        </Grid>
      </TabPanel>
    </>
  );

  const renderEmailConfiguration = () => (
    <>
      <MDTypography variant="h6" gutterBottom>
        Configuração de Email
      </MDTypography>

      <MDTypography variant="body2" color="text.secondary" mb={3}>
        Informe o email onde deseja receber as notificações e a frequência desejada.
      </MDTypography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <MDInput
            type="email"
            label="Email para notificações"
            fullWidth
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="seu.email@exemplo.com"
            required
          />
          <MDBox display="flex" justifyContent="flex-end" mt={1}>
            <MDButton
              variant="outlined"
              color="info"
              onClick={handleTestEmail}
              disabled={!email}
              size="small"
            >
              Enviar Email de Teste
            </MDButton>
          </MDBox>
        </Grid>
        <Grid item xs={12}>
          <MDInput
            select
            label="Frequência de envio"
            value={frequency}
            onChange={(e) => setFrequency(e.target.value)}
            fullWidth
            sx={{
              "& .MuiSelect-select": {
                padding: "0.75rem",
                minHeight: "1.4375em",
                height: "1.4375em",
                display: "flex",
                alignItems: "center",
              },
              "& .MuiInputBase-input": {
                height: "1.4375em",
              },
              "& .MuiOutlinedInput-root": {
                paddingTop: "0.75rem",
                paddingBottom: "0.75rem",
              },
            }}
          >
            <MenuItem value="daily">Diária</MenuItem>
            <MenuItem value="weekly">Semanal</MenuItem>
            <MenuItem value="monthly">Mensal</MenuItem>
          </MDInput>
        </Grid>

        <Grid item xs={12}>
          <Paper variant="outlined" sx={{ p: 2, mt: 2, backgroundColor: "#f8f9fa" }}>
            <MDTypography variant="subtitle2" gutterBottom color="info">
              Informações sobre a frequência de envio:
            </MDTypography>
            <MDBox component="ul" sx={{ pl: 2, mt: 1, mb: 0 }}>
              <li>
                <MDTypography variant="body2" color="text.secondary">
                  <strong>Diária:</strong> Emails serão enviados todos os dias às 7h da manhã
                </MDTypography>
              </li>
              <li>
                <MDTypography variant="body2" color="text.secondary">
                  <strong>Semanal:</strong> Emails serão enviados todas as segundas-feiras às 7h da
                  manhã
                </MDTypography>
              </li>
              <li>
                <MDTypography variant="body2" color="text.secondary">
                  <strong>Mensal:</strong> Emails serão enviados no primeiro dia de cada mês às 7h
                  da manhã
                </MDTypography>
              </li>
            </MDBox>
          </Paper>
        </Grid>
      </Grid>
    </>
  );

  const renderSummary = () => (
    <>
      <MDTypography variant="h6" gutterBottom>
        Resumo das Configurações
      </MDTypography>

      <Paper elevation={0} variant="outlined" sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={6}>
            <MDTypography variant="subtitle2">Email:</MDTypography>
            <MDTypography variant="body2" mb={2}>
              {email}
            </MDTypography>

            <MDTypography variant="subtitle2">Frequência:</MDTypography>
            <MDTypography variant="body2" mb={2}>
              {frequency === "daily" ? "Diária" : frequency === "weekly" ? "Semanal" : "Mensal"}
            </MDTypography>

            <MDTypography variant="subtitle2">Total de indicadores selecionados:</MDTypography>
            <MDTypography variant="body2">{countSelectedIndicators()}</MDTypography>
          </Grid>

          <Grid item xs={12} md={6}>
            <MDTypography variant="subtitle2" gutterBottom>
              Indicadores por categoria:
            </MDTypography>
            <MDBox component="ul" sx={{ pl: 2, mt: 0 }}>
              <li>
                <MDTypography variant="body2">
                  Inicial: {Object.values(dashboardIndicators).filter((v) => v).length}
                </MDTypography>
              </li>
              <li>
                <MDTypography variant="body2">
                  Trima: {Object.values(trimaIndicators).filter((v) => v).length}
                </MDTypography>
              </li>
              <li>
                <MDTypography variant="body2">
                  Reveos: {Object.values(reveosIndicators).filter((v) => v).length}
                </MDTypography>
              </li>
            </MDBox>
          </Grid>
        </Grid>
      </Paper>

      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <MDTypography variant="subtitle2">Ver indicaodres selecionados</MDTypography>
        </AccordionSummary>
        <AccordionDetails>
          {Object.values(dashboardIndicators).some((v) => v) && (
            <>
              <MDTypography variant="subtitle2" gutterBottom>
                Dashboard:
              </MDTypography>
              <Box component="ul" sx={{ pl: 2, mt: 0 }}>
                {Object.entries(dashboardIndicators)
                  .filter(([_, value]) => value)
                  .map(([key]) => (
                    <li key={key}>
                      <MDTypography variant="body2">{formatIndicatorLabel(key)}</MDTypography>
                    </li>
                  ))}
              </Box>
            </>
          )}

          {Object.values(trimaIndicators).some((v) => v) && (
            <>
              <MDTypography variant="subtitle2" gutterBottom sx={{ mt: 2 }}>
                Trima:
              </MDTypography>
              <Box component="ul" sx={{ pl: 2, mt: 0 }}>
                {Object.entries(trimaIndicators)
                  .filter(([_, value]) => value)
                  .map(([key]) => (
                    <li key={key}>
                      <MDTypography variant="body2">{formatIndicatorLabel(key)}</MDTypography>
                    </li>
                  ))}
              </Box>
            </>
          )}

          {Object.values(reveosIndicators).some((v) => v) && (
            <>
              <MDTypography variant="subtitle2" gutterBottom sx={{ mt: 2 }}>
                Reveos:
              </MDTypography>
              <Box component="ul" sx={{ pl: 2, mt: 0 }}>
                {Object.entries(reveosIndicators)
                  .filter(([_, value]) => value)
                  .map(([key]) => (
                    <li key={key}>
                      <MDTypography variant="body2">{formatIndicatorLabel(key)}</MDTypography>
                    </li>
                  ))}
              </Box>
            </>
          )}
        </AccordionDetails>
      </Accordion>
    </>
  );

  function getStepContent(step) {
    switch (step) {
      case 0:
        return renderIndicatorSelection();
      case 1:
        return renderEmailConfiguration();
      case 2:
        return renderSummary();
      default:
        return "Passo desconhecido";
    }
  }

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox py={3}>
        <Grid container justifyContent="center" spacing={3}>
          <Grid item xs={12} xl={10}>
            <Card>
              <MDBox p={3}>
                <MDTypography variant="h4" mb={3}>
                  Configuração de Notificações
                </MDTypography>

                <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
                  {steps.map((label) => (
                    <Step key={label}>
                      <StepLabel>{label}</StepLabel>
                    </Step>
                  ))}
                </Stepper>

                {getStepContent(activeStep)}

                <MDBox mt={4} display="flex" justifyContent="space-between">
                  <Button disabled={activeStep === 0} onClick={handleBack}>
                    Voltar
                  </Button>

                  {activeStep === steps.length - 1 ? (
                    <MDButton variant="gradient" color="info" onClick={handleSave}>
                      Salvar Configurações
                    </MDButton>
                  ) : (
                    <MDButton variant="contained" color="info" onClick={handleNext}>
                      Próximo
                    </MDButton>
                  )}
                </MDBox>
              </MDBox>
            </Card>
          </Grid>
        </Grid>
      </MDBox>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={5000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity={snackbarSeverity}
          variant="filled"
          sx={{ width: "100%" }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>

      <Footer />
    </DashboardLayout>
  );
}

export default Notifications;
