import React, { createContext, useContext, useState } from "react";
import PropTypes from "prop-types";

const ReveosDataContext = createContext();

export function ReveosDataProvider({ children }) {
  const [cache, setCache] = useState({
    data: null,
    timestamp: null,
    loading: false,
    promise: null,
  });

  const CACHE_DURATION = 5 * 60 * 1000; // 5 minutos em millisegundos

  const fetchData = async () => {
    const token = localStorage.getItem("authToken");
    const apiUrl = process.env.REACT_APP_API_URL;

    if (!token) {
      throw new Error("Nenhum token de autenticação encontrado");
    }

    const response = await fetch(`${apiUrl}/api/reveosComponentsVolume`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      if (response.status === 401 || response.status === 403) {
        localStorage.removeItem("authToken");
        window.location.href = "/authentication/sign-in";
        return null;
      }
      throw new Error(`Erro HTTP: ${response.status}`);
    }

    const data = await response.json();
    return data;
  };

  const getData = async () => {
    const now = Date.now();

    if (cache.data && cache.timestamp && now - cache.timestamp < CACHE_DURATION) {
      return cache.data;
    }

    if (cache.loading && cache.promise) {
      return cache.promise;
    }

    setCache((prev) => ({ ...prev, loading: true }));
    const promise = fetchData()
      .then((data) => {
        setCache({
          data,
          timestamp: Date.now(),
          loading: false,
          promise: null,
        });
        return data;
      })
      .catch((error) => {
        setCache((prev) => ({ ...prev, loading: false, promise: null }));
        throw error;
      });

    setCache((prev) => ({ ...prev, promise }));
    return promise;
  };

  return <ReveosDataContext.Provider value={{ getData }}>{children}</ReveosDataContext.Provider>;
}

// Adicionando PropTypes para o ReveosDataProvider
ReveosDataProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export function useReveosData() {
  return useContext(ReveosDataContext);
}
