import PropTypes from "prop-types"; // Importação do PropTypes
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import { NavLink } from "react-router-dom";

function Sidenav({ color, brand, brandName, routes, onMouseEnter, onMouseLeave, onLogout }) {
  const renderRoutes = routes.map(({ type, name, icon, key, route, action }) => {
    if (type === "collapse") {
      if (action === "logout") {
        return (
          <MDBox
            key={key}
            component="li"
            onClick={onLogout} // Chama a função de logout
            sx={{
              cursor: "pointer",
              py: 1,
              px: 3,
              "&:hover": { backgroundColor: "#f5f5f5" }, // Efeito hover consistente
            }}
          >
            <MDBox display="flex" alignItems="center">
              {icon}
              <MDTypography variant="button" ml={1}>
                {name}
              </MDTypography>
            </MDBox>
          </MDBox>
        );
      }
      return (
        <NavLink key={key} to={route} style={{ textDecoration: "none" }}>
          <MDBox
            component="li"
            sx={{
              py: 1,
              px: 3,
              "&:hover": { backgroundColor: "#f5f5f5" }, // Efeito hover consistente
            }}
          >
            <MDBox display="flex" alignItems="center">
              {icon}
              <MDTypography variant="button" ml={1}>
                {name}
              </MDTypography>
            </MDBox>
          </MDBox>
        </NavLink>
      );
    }
    return null;
  });

  return (
    <MDBox
      sx={{ height: "100vh", overflow: "auto", bgcolor: "grey.100" }}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
    >
      <MDBox component="img" src={brand} alt={brandName} width="100%" p={2} />
      <MDBox component="ul" sx={{ listStyle: "none", p: 0 }}>
        {renderRoutes}
      </MDBox>
    </MDBox>
  );
}

// Definição de PropTypes
Sidenav.propTypes = {
  color: PropTypes.string.isRequired,
  brand: PropTypes.string.isRequired,
  brandName: PropTypes.string.isRequired,
  routes: PropTypes.arrayOf(
    PropTypes.shape({
      type: PropTypes.string,
      name: PropTypes.string,
      icon: PropTypes.node,
      key: PropTypes.string,
      route: PropTypes.string,
      action: PropTypes.string,
    })
  ).isRequired,
  onMouseEnter: PropTypes.func,
  onMouseLeave: PropTypes.func,
  onLogout: PropTypes.func,
};

export default Sidenav;
